 //This 0 is a zero! } } here is the ScoreCounter script can you modify the previous one to match this one","completion":"Certainly! Here's the modified version of the MarioController script to work with the ScoreCounter script: \`\`\`csharp using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.SceneManagement; public class MarioController : MonoBehaviour { public float moveSpeed = 5f; public float jumpForce = 5f; public float ladderClimbSpeed = 2f; public int maxHealth = 3; private int currentHealth; private bool grounded; private bool onLadder; private bool climbingLadder; private Vector2 direction; private Rigidbody2D rb2d; private new Collider2D collider; private Collider2D[] results; private int currentSceneIndex; private ScoreCounter scoreCounter; private void Awake() { rb2d = GetComponent<Rigidbody2D>(); collider = GetComponent<Collider2D>(); results = new Collider2D[4]; scoreCounter = FindObjectOfType<ScoreCounter>(); // Reference to the score counter script } private void Start() { currentHealth = maxHealth; currentSceneIndex = SceneManager.GetActiveScene().buildIndex; } private void CheckCollision() { grounded = false; onLadder = false; Vector2 size = collider.bounds.size; size.y += 0.1f; size.x /= 2f; int amount = Physics2D.OverlapBoxNonAlloc(transform.position, size, 0f, results); for (int i = 0; i < amount; i++) { GameObject hit = results[i].gameObject; if (hit.layer == LayerMask.NameToLayer(\\"Platform\\")) { grounded = hit.transform.position.y < (transform.position.y - 0.5f); } else if (hit.layer == LayerMask.NameToLayer(\\"Ladder\\")) { onLadder = true; } } } private void OnCollisionEnter2D(Collision2D collision) { if (collision.gameObject.layer == LayerMask.NameToLayer(\\"Barrel\\")) { currentHealth--; ResetScene(); } else if (collision.gameObject.layer == LayerMask.NameToLayer(\\"Win\\")) { scoreCounter.score += 500; // Add +500 score SceneManager.LoadScene(\\"_Scene_DonkeyKong25M\\"); } } private void Res